components/sbthemes/module/sb_megamobile:
{{!--
Arguments:
	- moduleId (number): section instance index number
	- class_sfx (string): Use for CSS class styling suffix
--}}

{{#if display_mode '==' 'horizontal'}}
	{{#if status }} {{> components/sbthemes/module/sb_megamobile/navigation-menu}} {{/if}}
{{else}}
	{{#if status }} {{> components/sbthemes/module/sb_megamobile/navigation-vertical limit_item = limit_item}}{{/if}}
{{/if}}

{{#if status '==' 'true' }}
<li class="navPages-item {{#if type-menu '==' 'mega'}}hasMegamenu{{/if}} {{#if position '==' 'right'}}float-right{{/if}}">

{{#if children}}
    <a class="navPages-action has-subMenu" href="{{url}}">
		{{#if ../icon-awesome '!=' ' '}}
			<i class="fa fa-{{../icon-awesome }}"></i> 
		{{else if ../icon-image '!=' ' '}}
			<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
		{{/if}}

		{{#if ../badge '!=' ' '}}
			<span class="badge badge--{{../badge}}">{{../badge}}</span>
		{{/if}} 
        {{name}} 

		<span class=" has-subMenu" data-collapsible="navPages-{{id}}">
			<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
		</span>
    </a>
    <div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
		{{#if type-menu '==' 'mega'}}
			{{> components/sbthemes/module/sb_megamenu/horizontal/submega 
				columns = columns
				banner-mode = banner-mode
				banner-img 	= banner-img
				banner-placeholder = banner-placeholder
				banner-url 	= banner-url
			}}
		{{else}}
			<ul class="navPage-subMenu-list">
				{{#each children}}
					<li class="navPage-subMenu-item">
						{{#if children}}
							<a class="navPage-subMenu-action navPages-action navPages-action-depth-max has-subMenu" href="{{url}}">
								{{name}}
								<span class=" has-subMenu" 
									data-collapsible="navPages-{{id}}"
									">
									<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
								</span>
							</a>
							<div class="navPage-subMenu subMenu--level2" id="navPages-{{id}}" aria-hidden="false" tabindex="-1">
								<ul class="navPage-childList">

									{{#each children}}
										<li class="navPage-childList-item">
											{{#if children}}
												<a class="navPage-childList-action navPages-action navPages-action-depth-max has-subMenu" href="{{url}}" data-collapsible="navPages-{{id}}">
													{{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
												</a>
												{{> components/common/navigation-dropdown}}
											{{else}}
												<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
											{{/if}}
										</li>
									{{/each}}
								</ul>
							</div>


						{{else}}
							<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
						{{/if}}
					</li>
				{{/each}}
			</ul>
		{{/if}}
	</div>

{{else}}
	<a class="navPages-action" href="{{url}}">

		{{#if ../icon-awesome '!=' ' '}}
			<i class="fa fa-{{../icon-awesome }}"></i>
		{{else if ../icon-image '!=' ' '}}
			<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
		{{/if}}
		{{#if ../badge '!=' ' '}}
			<span class="badge badge--{{../badge}}">{{../badge}}</span>
		{{/if}} 

		{{name}}


	</a>

{{/if}}
</li>
{{/if}}

{{#if status '==' 'true' }}
<li class="navPages-item{{#if load-webpage '==' 'yes'}} hasMegamenu{{/if}}{{#if position '==' 'right'}} float-right {{/if}} ">
{{#if children}}
<a class="navPages-action has-subMenu " href="{{url}}" >
	{{#if ../icon-awesome '!=' ' '}}
		<i class="fa fa-{{../icon-awesome }}"></i>
	{{else if ../icon-image '!=' ' '}}
		<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
	{{/if}}
	
	{{#if ../badge '!=' ' '}}
		<span class="badge badge--{{../badge}}">{{../badge}}</span>
	{{/if}} 
	
	{{name}} 
	<span class=" has-subMenu" data-collapsible="navPages-mwebpage-{{@index}}">
		<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
	</span>
	
</a>
<div class="navPage-subMenu" id="navPages-mwebpage-{{@index}}" aria-hidden="true" tabindex="-1">
	
	<ul class="navPage-subMenu-list ">
		{{#forEach children}}
			<li class="navPage-subMenu-item">
				{{#if children}}
					<a
						class="navPage-subMenu-action navPages-action has-subMenu"
						href="{{url}}"
						>
						{{name}} 
						<span class=" has-subMenu" data-collapsible="subMenu-webpage-{{@index}}">
							<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
						</span>
						
					</a>
					<div class="navPage-subMenu subMenu--level2" id="subMenu-webpage-{{@index}}" aria-hidden="true" tabindex="-1">
						<ul class="navPage-childList" >
							{{#each children}}
							<li class="navPage-childList-item">
								<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
							</li>
							{{/each}}
							
						</ul>
					</div>
				{{else}}
					<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
				{{/if}}
				
			</li>
		{{/forEach}}
	</ul>
	
</div>
{{else}}

	<a class="navPages-action" href="{{url}}">
		
		{{#if ../icon-awesome '!=' ' '}}
			<i class="fa fa-{{../icon-awesome }}"></i>
		{{else if ../icon-image '!=' ' '}}
			<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
		{{/if}}
		{{#if ../badge '!=' ' '}}
			<span class="badge badge--{{../badge}}">{{../badge}}</span>
		{{/if}} 
		{{name}}
		
		{{#if ../load-webpage '==' 'yes'}}
		<span class=" has-subMenu" data-collapsible="navPages-mwebpage-{{@index}}">
			<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
		</span>
		{{/if}} 
	</a>
	
	{{#if ../load-webpage '==' 'yes'}}
	<div class="navPage-subMenu " id="navPages-mwebpage-{{@index}}" aria-hidden="true" tabindex="-1">
		{{> components/sbthemes/module/sb_megamenu/horizontal/subwebpage  webpage=url}}
	</div>
	{{/if}} 
{{/if}}
 </li>
{{/if}}

{{#if status '==' 'true' }}
<li class="navPages-item {{#if type-menu '==' 'default'}}navPages-item--{{type-menu}} {{/if}}">
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}">
	{{name}} 
	<span class=" has-subMenu" data-collapsible="navPages-mvertical-{{id}}">
		<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
	</span>
	
</a>
<div class="navPage-subMenu subMenu--{{../type-menu}}" id="navPages-mvertical-{{id}}" aria-hidden="true" tabindex="-1">
    {{#if ../type-menu '==' 'mega'}}
	<div class="row">
		{{#if ../banner_mode '==' 'vertical'}}
		<div class="col-lg-9">
		{{else if ../banner_mode '==' 'horizontal'}}
		<div class="col-lg-12">
		{{else}}
		<div class="col-lg-12">
		{{/if}}
		
			<ul class="navPage-subMenu-list grid-{{../content_column}}">
				{{#forEach children}}
					<li class="navPage-subMenu-item">
						{{#if children}}
							<a
								class="navPage-subMenu-action navPages-action has-subMenu"
								href="{{url}}"
							>
								{{name}} 
								
								<span class=" has-subMenu" 
								data-collapsible="navPages-mvertical-{{id}}"
								data-collapsible-disabled-breakpoint="medium"
								data-collapsible-disabled-state="open"
								data-collapsible-enabled-state="closed">
									<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
								</span>
								
							</a>
							<ul class="navPage-childList" id="navPages-mvertical-{{id}}">
								{{#each children}}
								<li class="navPage-childList-item">
									<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
								</li>
								{{/each}}
								
							</ul>
						{{else}}
							<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
						{{/if}}
						
					</li>
				{{/forEach}}
			</ul>
		</div>
		
		{{#if ../banner_mode '==' 'vertical'}}
			{{#if ../banner_enable '==' 'yes'}}
			<div class="col-lg-3">
				<div class="banners">
					<div>
						{{#if banner_image '!=' ' '}}
							<a href="{{../banner_url}}"><img data-sizes="auto" class="img-fluid lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../banner_image}}" alt="banner"></a> 
						{{else}}
							<a href="{{../banner_url}}"><img class="img-fluid" src="{{cdn placeholder}}" alt="{{banner}}" /></a>
						{{/if}}
					</div>
				</div>
			</div>
			{{/if}}
		{{else if ../banner_mode '==' 'horizontal'}}
			{{#if ../banner_enable '==' 'yes'}}
			<div class="col-lg-12">
				<div class="banners">
					<div>
						{{#if banner_image '!=' ' '}}
							<a href="{{../banner_url}}"><img data-sizes="auto" class="img-fluid lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../banner_image}}" alt="banner"></a> 
						{{else}}
							<a href="{{../banner_url}}"><img class="img-fluid" src="{{cdn placeholder}}" alt="{{banner}}" /></a>
						{{/if}}
					</div>
				</div>
			</div>
			{{/if}}
		{{/if}}
		
	</div>
	{{else}}
		<ul class="navPage-subMenu-list">
			{{#forEach children}}
				<li class="navPage-subMenu-item">
					{{#if children}}
						<a
							class="navPage-subMenu-action navPages-action has-subMenu"
							href="{{url}}"
						>
							{{name}} 
							
							<span class=" has-subMenu" 
							data-collapsible="navPages-mvertical-{{id}}">
								<i class="icon navPages-action-moreIcon" aria-hidden="false"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
							</span>
							
						</a>
						<div class="navPage-subMenu subMenu--default subMenu--level2" id="navPages-mvertical-{{id}}" aria-hidden="false" tabindex="-1">
							<ul class="navPage-childList navPage-childList-default" >
								{{#each children}}
								<li class="navPage-childList-item">
									<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
								</li>
								{{/each}}
								
							</ul>
						</div>
					{{else}}
						<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
					{{/if}}
					
				</li>
			{{/forEach}}
		</ul>
	{{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}">
	{{name}}
</a>
{{/if}}
</li>
{{/if}}
