components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} 
        <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu dropdown-menu-right" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                        {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/sbthemes/module/sb_megamenu/d:
{{!--
Arguments:
	- moduleId (number): section instance index number
	- class_sfx (string): Use for CSS class styling suffix
--}}

{{#if display_mode '==' 'horizontal'}}
