{{/if}}

components/sbthemes/module/sb_megamenu/h:
{{#if status '==' 'true' }}
<li class="navPages-item{{#if load-webpage '==' 'yes'}} hasMegamenu{{/if}}{{#if position '==' 'right'}} float-right {{/if}} ">
{{#if children}}
<a class="navPages-action has-subMenu " href="{{url}}" >
	{{#if ../icon-awesome '!=' ' '}}
		<i class="fa fa-{{../icon-awesome }}"></i>
	{{else if ../icon-image '!=' ' '}}
		<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
	{{/if}}
	
	{{#if ../badge '!=' ' '}}
		<span class="badge badge--{{../badge}}">{{../badge}}</span>
	{{/if}} 
	
	{{name}} 
	<span class=" has-subMenu" data-collapsible="navPages-webpage-{{@index}}">
		<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
	</span>
	
</a>
<div class="navPage-subMenu" id="navPages-webpage-{{@index}}" aria-hidden="true" tabindex="-1">
<div class="row">
    <div class="col-lg-12">
	        <h3 class="category-name">{{name}}</h3>
	    </div>
<div class="col-lg-12">
	<ul class="navPage-subMenu-list grid-{{../content_column}}">
		{{#forEach children}}
			<li class="navPage-subMenu-item">
				{{#if children}}
					<a
						class="navPage-subMenu-action navPages-action has-subMenu"
						href="{{url}}"
						>
						{{name}} 
						<span class=" has-subMenu" data-collapsible="subMenu-webpage-{{@index}}">
							<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
						</span>
						
					</a>
					<div class="navPage-subMenu subMenu--level2" id="subMenu-webpage-{{@index}}" aria-hidden="true" tabindex="-1">
						<ul class="navPage-childList" >
							{{#each children}}
							<li class="navPage-childList-item">
								<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
							</li>
							{{/each}}
							
						</ul>
					</div>
				{{else}}
					<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
				{{/if}}
				
			</li>
		{{/forEach}}
	</ul>
</div>
</div>	
</div>
{{else}}

	<a class="navPages-action" href="{{url}}">
		
		{{#if ../icon-awesome '!=' ' '}}
			<i class="fa fa-{{../icon-awesome }}"></i>
		{{else if ../icon-image '!=' ' '}}
			<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
		{{/if}}
		{{#if ../badge '!=' ' '}}
			<span class="badge badge--{{../badge}}">{{../badge}}</span>
		{{/if}} 
		{{name}}
		
		{{#if ../load-webpage '==' 'yes'}}
		<span class=" has-subMenu" data-collapsible="navPages-webpage-{{@index}}">
			<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
		</span>
		{{/if}} 
	</a>
	
	{{#if ../load-webpage '==' 'yes'}}
	<div class="navPage-subMenu " id="navPages-webpage-{{@index}}" aria-hidden="true" tabindex="-1">
		{{> components/sbthemes/module/sb_megamenu/horizontal/subwebpage  webpage=url}}
	</div>
	{{/if}} 
{{/if}}
 </li>
{{/if}}

components/sbthemes/module/sb_megamenu/v:
{{#if status '==' 'true' }}
<li class="navPages-item {{#if type-menu '==' 'default'}}navPages-item--{{type-menu}} {{/if}}">
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}">
	{{#if ../icon-awesome '!=' ' '}}
		<i class="fa fa-{{../icon-awesome }}"></i>
	{{else if ../icon-image '!=' ' '}}
		<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
	{{/if}}
	{{name}} 
	<span class=" has-subMenu" data-collapsible="navPages-vertical-{{id}}">
		<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
	</span>
	
</a>
<div class="navPage-subMenu subMenu--{{../type-menu}}" id="navPages-vertical-{{id}}" aria-hidden="true" tabindex="-1">
    {{#if ../type-menu '==' 'mega'}}
	<div class="row">
	    
	    <div class="col-lg-12">
	        <h3 class="category-name">{{name}}</h3>
	    </div>
		{{#if ../banner_mode '==' 'vertical'}}
		<div class="col-lg-9">
		{{else if ../banner_mode '==' 'horizontal'}}
		<div class="col-lg-12">
		{{else}}
		<div class="col-lg-12">
		{{/if}}
		
			<ul class="navPage-subMenu-list grid-{{../content_column}}">
				{{#forEach children}}
					<li class="navPage-subMenu-item">
						{{#if children}}
							<a
								class="navPage-subMenu-action navPages-action has-subMenu"
								href="{{url}}"
							>
								{{name}} 
								
								<span class=" has-subMenu" 
								data-collapsible="navPages-vertical-{{id}}"
								data-collapsible-disabled-breakpoint="medium"
								data-collapsible-disabled-state="open"
								data-collapsible-enabled-state="closed">
									<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
								</span>
								
							</a>
							<ul class="navPage-childList" id="navPages-vertical-{{id}}">
								{{#each (limit children 10)}}
								<li class="navPage-childList-item">
									<a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
								</li>
								{{/each}}
								
								
							</ul>
						{{else}}
							<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
						{{/if}}
						
					</li>
				{{/forEach}}
			</ul>
		</div>
		
		{{#if ../banner_mode '==' 'vertical'}}
			{{#if ../banner_enable '==' 'yes'}}
			<div class="col-lg-3">
				<div class="banners">
					<div>
						{{#if banner_image '!=' ' '}}
							<a href="{{../banner_url}}"><img data-sizes="auto" class="img-fluid lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../banner_image}}" alt="banner"></a> 
						{{else}}
							<a href="{{../banner_url}}"><img class="img-fluid" src="{{cdn placeholder}}" alt="{{banner}}" /></a>
						{{/if}}
					</div>
				</div>
			</div>
			{{/if}}
		{{else if ../banner_mode '==' 'horizontal'}}
			{{#if ../banner_enable '==' 'yes'}}
			<div class="col-lg-12">
				<div class="banners">
					<div>
						{{#if banner_image '!=' ' '}}
							<a href="{{../banner_url}}"><img data-sizes="auto" class="img-fluid lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../banner_image}}" alt="banner"></a> 
						{{else}}
							<a href="{{../banner_url}}"><img class="img-fluid" src="{{cdn placeholder}}" alt="{{banner}}" /></a>
						{{/if}}
					</div>
				</div>
			</div>
			{{/if}}
		{{/if}}
		
	</div>
	{{else}}
		<ul class="navPage-subMenu-list">
			{{#forEach children}}
				<li class="navPage-subMenu-item">
					{{#if children}}
						<a
							class="navPage-subMenu-action navPages-action has-subMenu"
							href="{{url}}"
						>
							{{name}} 
							
							<span class=" has-subMenu" 
							data-collapsible="navPages-vertical-{{id}}">
								<i class="icon navPages-action-moreIcon" aria-hidden="false"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
							</span>
							
						</a>
						<div class="navPage-subMenu subMenu--default subMenu--level2" id="navPages-vertical-{{id}}" aria-hidden="false" tabindex="-1">
							<ul class="navPage-childList" >
								{{#each (limit children 10)}}
								<li class="navPage-childList-item">
									<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
								</li>
								{{/each}}
								 <li><a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'common.view_all'}}</a></li>
							</ul>
						</div>
					{{else}}
						<a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
					{{/if}}
					
				</li>
			{{/forEach}}
		</ul>
	{{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}">
	{{#if ../icon-awesome '!=' ' '}}
		<i class="fa fa-{{../icon-awesome }}"></i>
	{{else if ../icon-image '!=' ' '}}
		<img data-sizes="auto" class="navPages__icon lazyload" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{cdn ../icon-image }}" alt="icons">
	{{/if}}
	{{name}}
</a>
{{/if}}
</li>
{{/if}}

components/sbthemes/module/sb_promotion/:
{{#if status }}
<div class="module sb-promotion sb-promotion--{{prefix}} {{class_sfx}}">
	<div class="block-content clearfix">
	{{#if limitation '>' 1}}
		<div class="sb-promotion--carousel"
           data-slick='{
				"dots": false,
				"arrows": false,
				"mobileFirst": true,
				"slidesToShow": 1,
				"slidesToScroll": 1,
				"vertical": true,
				"verticalSwiping": true,
				"autoplay": true,
				"autoplaySpeed": 5000
			}'
    	>
		{{#for  limitation }}
			{{> components/sbthemes/module/sb_promotion/default-item 
				url		= (lang  (concat (concat (concat (concat 'sb_revo.' ../prefix) '.item') $index)'.url'))
				title	= (lang  (concat (concat (concat (concat 'sb_revo.' ../prefix) '.item') $index)'.title'))
			}}
		{{/for}}
		</div>
	{{else}}
		{{> components/sbthemes/module/sb_promotion/default-item 
			url		= (lang    (concat (concat 'sb_revo.' ../prefix) '.item1.url'))
			title	= (lang    (concat (concat 'sb_revo.' ../prefix) '.item1.title'))
		}}
	{{/if}}
	</div>
</div>
{{/if}}
