components/sbthemes/module/sb_listing_ta:
{{!--
	Arguments:
		- category_id (number)
		- style (string): CSS class styling suffix
		- banner
		- url
		- alignBanner
	
	
	Global variables:
		- categories (array)
	
	--}}
	
	
	{{#if category_id '!==' ' '}}
	{{#withHash
		categories               = categories
		category_id              = category_id
		alignBanner               = alignBanner
		banner                   = banner
		placeholder              = placeholder
		url                      = url
		theme_settings           = theme_settings
	}}
	
	{{#each categories}}
		{{#if id '===' ../category_id}}
			{{#if (merge ../../this
				category_name = name
				category_url = url)}}
			{{/if}}
		{{else}}
			{{#each children}}
				{{#if id '===' ../../../category_id}}
					{{#if (merge ../../../../this
						category_name = name
						category_url = url)}}
					{{/if}}
				{{/if}}
			{{/each}}
		{{/if}}
	{{/each}}
	
	{{#if category_url}}
	<div id="sb-listing-tabs{{category_id}}" class="sb-listing-tabs">
	
	{{#each categories}}
	{{#if id '===' ../category_id}}
		<div class="ltabs-container d-flex">
			<div class="ltabs-heading">
	
			<h3 class="block-title"><a href="{{url}}" title="{{name}}">{{name}}</a></h3>
	
			<ul class="tabs ltabs-tabs" data-tab data-tab-sblisting role="tablist">
	
	
			{{#each (limit children 10)}}
						<li class="tab" role="presentation"><a class="tab-title" href="#tab-sblisting-bestselling-{{id}}" role="tab" tabindex="0" aria-selected="true" aria-controls="tab-sblisting-bestselling-{{id}}">{{name}}</a></li>
	
			{{/each}}
			  <li class="tab"><a class="tab-title" href="#"><strong>Shop All</strong></a></li>
		</ul>
	
		</div>
	   {{/if}}
	{{/each}}
	
	
	
	
	
			{{#if alignBanner '==' 'no'}}
			<div class="ltabs-items-container ltabs-items-container--noimage">
			{{else}}
			<div class="ltabs-items-container ltabs-items-container--image">
			{{/if}}
			{{#each categories}}
				
				{{#if id '==' ../category_id}}
				<section role="tabpanel" aria-hidden="false" class="tab-content is-active" id="tab-sblisting-bestselling-{{id}}"
					data-items-sblisting="{{concat url '?sort=bestselling'}}"
				>
					{{> components/sbthemes/module/sb_listing_tabs/product-loading }}
				</section>
				{{/if}}
			
			{{/each}}
			</div>
	
			{{#if alignBanner '==' 'right'}}
			<div class="ltabs-items-image">
				<div class="banners" {{#if theme_settings.general_fadeimage}}data-fade-image data-size-image{{/if}} >
					<div class="banner-figure">
						{{#if banner '!=' ' '}}
							<a href="{{url}}" title="{{category_name}}"><img  class="img-fluid" src="{{cdn banner}}" alt="{{category_name}}" /></a>
						{{else}}
							<a href="{{url}}" title="{{category_name}}"><img class="img-fluid" src="{{cdn placeholder}}" alt="{{category_name}}"  /></a>
						{{/if}}
					</div>
				</div>
	
			</div>
			{{/if}}
	
		</div>
	</div>
	{{else}}
		<div class="alert alert-info" role="alert" style="margin:0px">
			<i class="fa fa-info-circle"></i>
		   {{{lang 'sb_themes.sb_listing_tabs.noCategory' }}}
		</div>
	{{/if}}
	
	{{/withHash}}
	
	
	{{/if}}

components/sbthemes/sections/section-hom:
{{!--
    Arguments: Module banner
        - status    	(boolean):is display state of add on
        - prefix 		(string): Prefixes are added to the banner
        - showContent   (boolean): is display show content of banner
        - limitation    (number): The number of banner item to display.
        - display_mode  (string) : Support format  (horizontal , vertical)
        - class_sfx     (string) : Class suffix for module.
    
    Arguments:Module sb_supper_category & so_category_slider
        - status    	(boolean):is display state of add on
        - moduleId (number): section instance index number
        - limit_subcategory  (number): The number of sub category item to display.
        - showBanner   (boolean): is display show  banner
        - class_sfx (string) : Class suffix for module.
    
    --}}
    
    {{#if   section '==' 'section1'}}
    {{> components/sbthemes/module/sb_listing_tabs/default status=true moduleId=1  banner="left" class_sfx=""}}
    {{else if   section '==' 'section2'}}
    {{> components/sbthemes/module/sb_listing_tabs/default status=true moduleId=2  banner="left" class_sfx=""}}
    
    {{else if 	section '==' 'section4'}}  	{{> components/sbthemes/module/sb_listing_tabs/default status=true moduleId=4  banner="left" class_sfx=""}}
        
    {{else if 	section '==' 'section6'}}	
    {{else if 	section '==' 'section7'}}	{{> components/sbthemes/module/sb_banner/banner status=true prefix="blockBanner2" showContent=false limitation=1 class_sfx=""}}
    {{else if 	section '==' 'section8'}}
                                            {{> components/sbthemes/module/sb_extraslider/default status=true products= products.new moduleId=1  row=3 columns=1 banner="left" class_sfx=" extraslider--new" }}
                                            {{> components/sbthemes/module/sb_extraslider/default status=true products= products.featured moduleId=2  row=3 columns=1 banner="left" class_sfx="extraslider--featured" }}
    {{else if 	section '==' 'section9'}}	{{> components/sbthemes/module/sb_latest_blog/default status=true blogs= blog.recent_posts  show_desc="no" hide_heading=false}}
    {{else if 	section '==' 'section10'}}	{{> components/sbthemes/module/sb_brand/default status=true prefix="static_brand" limitation=10}}
    
    {{/if}}
